class Solution2Test {

    Solution2 solution2 = new Solution2();

    @Test
    void test1() {
        String result = solution2.longestPalindrome("babad");
        assertTrue(result.equals("bab")||result.equals("aba"));
    }

    @Test
    void test2() {
        String result = solution2.longestPalindrome("cbbd");
        assertEquals("bb",result);
    }

    @Test
    void test3() {
        String result = solution2.longestPalindrome("abb");
        assertEquals("bb",result);
    }

    @Test
    void test4() {
        String result = solution2.longestPalindrome("abacab");
        assertEquals("bacab",result);
    }

    @Test
    void test5() {
        String result = solution2.longestPalindrome("a");
        assertEquals("a",result);
    }
    @Test
    void test6() {
        String result = solution2.longestPalindrome("aaabaaaa");
        assertEquals("aaabaaa",result);
    }

    @Test
    void test7() {
        String result = solution2.longestPalindrome("");
        assertEquals("gykrkyg",result);
    }
    @Test
    void test8() {
        String result = solution2.longestPalindrome("");
        assertEquals("",result);
    }

}